import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [message, setMessage] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    let ws;
    let reconnectAttempts = 0;
    const maxReconnectAttempts = 5;

    const connectWebSocket = () => {
      ws = new WebSocket('ws://localhost:8002/ws/prices');

      ws.onopen = () => {
        console.log('WebSocket connected');
        reconnectAttempts = 0;
        setError(null);
      };

      ws.onmessage = (event) => {
        console.log('Received WebSocket message:', event.data);
        try {
          const result = JSON.parse(event.data);
          setMessage(result.message);
          setError(null);
        } catch (e) {
          console.error('Error parsing WebSocket message:', e);
          setError('Error parsing message');
        }
      };

      ws.onerror = (error) => {
        console.error('WebSocket error:', error);
        setError('WebSocket connection failed');
      };

      ws.onclose = () => {
        console.log('WebSocket closed');
        setError('WebSocket connection closed');
        if (reconnectAttempts < maxReconnectAttempts) {
          reconnectAttempts++;
          console.log(`Reconnecting WebSocket... Attempt ${reconnectAttempts}`);
          setTimeout(connectWebSocket, 3000);
        } else {
          setError('Max reconnect attempts reached. Please refresh the page.');
        }
      };
    };

    connectWebSocket();

    return () => {
      if (ws) ws.close();
    };
  }, []);

  if (error) return <div>Error: {error}</div>;
  if (!message) return <div>Loading...</div>;

  return (
    <div className="App">
      <h1>Cost Catcher - WebSocket Test</h1>
      <div className="category">
        <h2>WebSocket Message</h2>
        <div>{message}</div>
      </div>
    </div>
  );
}

export default App;
