import React, { useState, useEffect } from 'react';
import './App.css';
import UberLogo from './assets/uber-logo.png';
import LyftLogo from './assets/lyft-logo.png';

function App() {
  const [ridesData, setRidesData] = useState(null);
  const [error, setError] = useState(null);
  const [pickupAddress, setPickupAddress] = useState('');
  const [dropoffAddress, setDropoffAddress] = useState('');
  const [cabType, setCabType] = useState('Economy');
  const [ws, setWs] = useState(null);
  const [isWsReady, setIsWsReady] = useState(false);

  useEffect(() => {
    const websocket = new WebSocket('ws://localhost:8002/ws/prices');

    websocket.onopen = () => {
      console.log('WebSocket connected');
      setIsWsReady(true);
      websocket.send(JSON.stringify({
        category: 'ride',
        pickup: pickupAddress,
        dropoff: dropoffAddress,
        cabType: cabType,
      }));
    };

    websocket.onmessage = (event) => {
      console.log('Received WebSocket message:', event.data);
      try {
        const result = JSON.parse(event.data);
        setRidesData(result);
        setError(null);
      } catch (e) {
        console.error('Error parsing WebSocket message:', e);
        setError('Error parsing price data');
      }
    };

    websocket.onerror = (error) => {
      console.error('WebSocket error:', error);
      setError('WebSocket connection failed');
      setIsWsReady(false);
    };

    websocket.onclose = () => {
      console.log('WebSocket disconnected');
      setError('WebSocket connection closed');
      setIsWsReady(false);
    };

    setWs(websocket);

    return () => {
      websocket.close();
    };
  }, []);

  useEffect(() => {
    if (ws && isWsReady) {
      console.log('Sending updated parameters:', { pickupAddress, dropoffAddress, cabType });
      ws.send(JSON.stringify({
        category: 'ride',
        pickup: pickupAddress,
        dropoff: dropoffAddress,
        cabType: cabType,
      }));
    }
  }, [pickupAddress, dropoffAddress, cabType, ws, isWsReady]);

  if (error) return <div className="error">Error: {error}</div>;

  // Map provider names to logos
  const providerLogos = {
    Uber: UberLogo,
    Lyft: LyftLogo,
  };

  return (
    <div className="App">
      <h1>Cost Catcher - Ride Price Comparison</h1>

      <div className="tab-content">
        <div className="address-inputs">
          <h3>Enter Ride Details</h3>
          <div>
            <label>Pickup Address: </label>
            <input
              type="text"
              value={pickupAddress}
              onChange={(e) => setPickupAddress(e.target.value)}
              placeholder="e.g., 123 Main St, City"
            />
          </div>
          <div>
            <label>Drop-off Address: </label>
            <input
              type="text"
              value={dropoffAddress}
              onChange={(e) => setDropoffAddress(e.target.value)}
              placeholder="e.g., 456 Oak St, City"
            />
          </div>
          <div>
            <label>Cab Type: </label>
            <select value={cabType} onChange={(e) => setCabType(e.target.value)}>
              <option value="Economy">Economy</option>
              <option value="Premium">Premium</option>
              <option value="SUV">SUV</option>
            </select>
          </div>
        </div>

        {ridesData ? (
          <div className="category">
            <h2>Rides</h2>
            <div>
              {ridesData.results
                .filter(r => r.category === 'ride')
                .map(r => (
                  <div key={r.provider} className="provider">
                    <img src={providerLogos[r.provider]} alt={`${r.provider} logo`} />
                    ${r.price.toFixed(2)}
                    {ridesData.cheapest.ride && ridesData.cheapest.ride.provider === r.provider && (
                      <div className="car-container">
                        <span className="car">🚗</span>
                      </div>
                    )}
                  </div>
                ))}
            </div>
            <div className="cheapest">
              {ridesData.cheapest.ride ? (
                `Cheapest: ${ridesData.cheapest.ride.provider} at $${ridesData.cheapest.ride.price.toFixed(2)}`
              ) : (
                'No ride prices available'
              )}
            </div>
            <div className="savings">
              {ridesData.savings.ride > 0
                ? `You save $${ridesData.savings.ride.toFixed(2)} by choosing the cheapest!`
                : 'No savings available'}
            </div>
          </div>
        ) : (
          <div>Loading...</div>
        )}
      </div>
    </div>
  );
}

export default App;
