import React, { useState, useEffect } from 'react';
import Lottie from 'lottie-react';
import './App.css';
import cab1Logo from './assets/cab1-logo.png';
import cab2Logo from './assets/cab2-logo.png';
import EconomyAnimation from './assets/animations/economy-car.json';
import QuickestAnimation from './assets/animations/quickest-car.json';
import WaitAndSaveAnimation from './assets/animations/wait-and-save.json';
import MinivanAnimation from './assets/animations/minivan.json';
import BlackSUVAnimation from './assets/animations/black-suv.json';
import RideMap from './RideMap'; // ✅ Import the map component

function App() {
  const [ridesData, setRidesData] = useState(null);
  const [error, setError] = useState(null);
  const [pickupAddress, setPickupAddress] = useState('');
  const [dropoffAddress, setDropoffAddress] = useState('');
  const [cabType, setCabType] = useState('Economy');
  const [ws, setWs] = useState(null);
  const [isWsReady, setIsWsReady] = useState(false);
  const [pickupCoords, setPickupCoords] = useState(null);   // ✅ new state
  const [dropoffCoords, setDropoffCoords] = useState(null); // ✅ new state

  useEffect(() => {
    const websocket = new WebSocket('ws://localhost:8002/ws/prices');

    websocket.onopen = () => {
      console.log('WebSocket connected');
      setIsWsReady(true);
      websocket.send(JSON.stringify({
        category: 'ride',
        pickup: pickupAddress,
        dropoff: dropoffAddress,
        cabType: cabType,
      }));
    };

    websocket.onmessage = (event) => {
      console.log('Received WebSocket message:', event.data);
      try {
        const result = JSON.parse(event.data);
        setRidesData(result);
        setError(null);

        // ✅ Set coordinates if available from backend
        if (result.pickup_coords && result.dropoff_coords) {
          setPickupCoords(result.pickup_coords);
          setDropoffCoords(result.dropoff_coords);
        }

      } catch (e) {
        console.error('Error parsing WebSocket message:', e);
        setError('Error parsing price data');
      }
    };

    websocket.onerror = (error) => {
      console.error('WebSocket error:', error);
      setError('WebSocket connection failed');
      setIsWsReady(false);
    };

    websocket.onclose = () => {
      console.log('WebSocket disconnected');
      setError('WebSocket connection closed');
      setIsWsReady(false);
    };

    setWs(websocket);

    return () => {
      websocket.close();
    };
  }, []);

  useEffect(() => {
    if (ws && isWsReady) {
      console.log('Sending updated parameters:', { pickupAddress, dropoffAddress, cabType });
      ws.send(JSON.stringify({
        category: 'ride',
        pickup: pickupAddress,
        dropoff: dropoffAddress,
        cabType: cabType,
      }));
    }
  }, [pickupAddress, dropoffAddress, cabType, ws, isWsReady]);

  if (error) return <div className="error">Error: {error}</div>;

  const providerLogos = {
    cab1: cab1Logo,
    cab2: cab2Logo,
  };

  const providerNames = {
    cab1: 'Kuber',
    cab2: 'Dryft',
  };

  const cabAnimations = {
    Economy: EconomyAnimation,
    Quickest: QuickestAnimation,
    WaitAndSave: WaitAndSaveAnimation,
    Minivan: MinivanAnimation,
    BlackSUV: BlackSUVAnimation,
  };

  return (
    <div className="App">
      <h1>Cost Catcher - Ride Price Comparison</h1>

      <div className="tab-content">
        <div className="address-inputs">
          <h3>Enter Ride Details</h3>
          <div>
            <label>Pickup Address: </label>
            <input
              type="text"
              value={pickupAddress}
              onChange={(e) => setPickupAddress(e.target.value)}
              placeholder="e.g., 123 Main St, City"
            />
          </div>
          <div>
            <label>Drop-off Address: </label>
            <input
              type="text"
              value={dropoffAddress}
              onChange={(e) => setDropoffAddress(e.target.value)}
              placeholder="e.g., 456 Oak St, City"
            />
          </div>
          <div>
            <label>Cab Type: </label>
            <select value={cabType} onChange={(e) => setCabType(e.target.value)}>
              <option value="Economy">Economy</option>
              <option value="Quickest">Quickest (under 2 mins)</option>
              <option value="WaitAndSave">Wait and Save (10-15 mins)</option>
              <option value="Minivan">Minivan (5+ seats)</option>
              <option value="BlackSUV">Black SUV</option>
            </select>
          </div>
          <div className="note">
            Note: For best results, please enter addresses in the format "Street Address, City, State ZIP" (e.g., 123 Main St, Los Angeles, CA 90001).
          </div>
        </div>

        <div className="cab-animation">
          <Lottie
            animationData={cabAnimations[cabType]}
            loop={true}
            style={{ width: 150, height: 150 }}
          />
        </div>

        {/* ✅ Render the map if we have both coordinates */}
        {pickupCoords && dropoffCoords && (
          <RideMap pickupCoords={pickupCoords} dropoffCoords={dropoffCoords} />
        )}

        {ridesData ? (
          <div className="category">
            <h2>Rides</h2>
            <div>
              {ridesData.results
                .filter(r => r.category === 'ride')
                .map(r => (
                  <div key={r.provider} className="provider">
                    <img
                      src={providerLogos[r.provider]}
                      alt={`${providerNames[r.provider]} logo`}
                      className={
                        ridesData.cheapest.ride && ridesData.cheapest.ride.provider === r.provider
                          ? 'cheapest-logo'
                          : ''
                      }
                    />
                    {r.price !== null ? `$${r.price.toFixed(2)}` : 'N/A'}
                    {r.error && (
                      <div style={{ color: 'red', fontSize: '12px' }}>
                        {r.error}
                      </div>
                    )}
                  </div>
                ))}
            </div>
            <div className="cheapest">
              {ridesData.cheapest.ride ? (
                `Cheapest: ${providerNames[ridesData.cheapest.ride.provider]} at $${ridesData.cheapest.ride.price.toFixed(2)}`
              ) : (
                'No ride prices available'
              )}
            </div>
            <div className="savings">
              {ridesData.savings.ride > 0
                ? `You save $${ridesData.savings.ride.toFixed(2)} by choosing the cheapest!`
                : 'No savings available'}
            </div>
          </div>
        ) : (
          <div>Loading...</div>
        )}
      </div>
    </div>
  );
}

export default App;
