// src/components/RideMap.js

import React from 'react';
import { MapContainer, TileLayer, Marker, Polyline } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

// These imports fix broken marker icons in many setups (especially Webpack+Leaflet)
import 'leaflet-defaulticon-compatibility';
import 'leaflet-defaulticon-compatibility/dist/leaflet-defaulticon-compatibility.css';

/**
 * RideMap Component
 * @param {Object} props
 * @param {[number, number]} props.pickupCoords - [latitude, longitude]
 * @param {[number, number]} props.dropoffCoords - [latitude, longitude]
 */
function RideMap({ pickupCoords, dropoffCoords }) {
  if (!pickupCoords || !dropoffCoords) return null;

  return (
    <div className="map-wrapper">
      <MapContainer
        center={pickupCoords}
        zoom={12}
        scrollWheelZoom={false}
        className="leaflet-container"
      >
        <TileLayer
          attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <Marker position={pickupCoords} />
        <Marker position={dropoffCoords} />
        <Polyline positions={[pickupCoords, dropoffCoords]} color="blue" />
      </MapContainer>
    </div>
  );
}

export default RideMap;
