import React, { useState, useEffect } from 'react';
import './App.css';
import UberLogo from './assets/uber-logo.png';
import LyftLogo from './assets/lyft-logo.png';
import Lottie from 'react-lottie';
import economyCarAnimation from './assets/animations/economy-car.json';
import quickestCarAnimation from './assets/animations/quickest-car.json';
import waitAndSaveAnimation from './assets/animations/wait-and-save.json'; // Single animation
import minivanAnimation from './assets/animations/minivan.json';
import blackSUVAnimation from './assets/animations/black-suv.json';

function App() {
  const [ridesData, setRidesData] = useState(null);
  const [error, setError] = useState(null);
  const [pickupAddress, setPickupAddress] = useState('');
  const [dropoffAddress, setDropoffAddress] = useState('');
  const [cabType, setCabType] = useState('Economy');
  const [ws, setWs] = useState(null);
  const [isWsReady, setIsWsReady] = useState(false);
  const [calculateTrigger, setCalculateTrigger] = useState(false);

  useEffect(() => {
    const websocket = new WebSocket('ws://localhost:8002/ws/prices');

    websocket.onopen = () => {
      console.log('WebSocket connected');
      setIsWsReady(true);
    };

    websocket.onmessage = (event) => {
      console.log('Received WebSocket message:', event.data);
      try {
        const result = JSON.parse(event.data);
        setRidesData(result);
        setError(null);
      } catch (e) {
        console.error('Error parsing WebSocket message:', e);
        setError('Error parsing price data');
      }
    };

    websocket.onerror = (error) => {
      console.error('WebSocket error:', error);
      setError('WebSocket connection failed');
      setIsWsReady(false);
    };

    websocket.onclose = () => {
      console.log('WebSocket disconnected');
      setError('WebSocket connection closed');
      setIsWsReady(false);
    };

    setWs(websocket);

    return () => {
      websocket.close();
    };
  }, []);

  const handleCalculate = () => {
    if (ws && isWsReady) {
      console.log('Sending parameters:', { pickupAddress, dropoffAddress, cabType });
      ws.send(JSON.stringify({
        category: 'ride',
        pickup: pickupAddress,
        dropoff: dropoffAddress,
        cabType: cabType,
      }));
      setCalculateTrigger(true);
    }
  };

  if (error) return <div className="error">Error: {error}</div>;

  const providerLogos = {
    Uber: UberLogo,
    Lyft: LyftLogo,
  };

  // Lottie animation options for each cab type
  const animationOptions = {
    Economy: {
      animationData: economyCarAnimation,
      loop: true,
      autoplay: true,
    },
    Quickest: {
      animationData: quickestCarAnimation,
      loop: true,
      autoplay: true,
    },
    WaitAndSave: {
      animationData: waitAndSaveAnimation,
      loop: true,
      autoplay: true,
    },
    Minivan: {
      animationData: minivanAnimation,
      loop: true,
      autoplay: true,
    },
    BlackSUV: {
      animationData: blackSUVAnimation,
      loop: true,
      autoplay: true,
    },
  };

  return (
    <div className="App">
      <h1>Cost Catcher - Ride Price Comparison</h1>

      <div className="tab-content">
        <div className="address-inputs">
          <h3>Enter Ride Details</h3>
          <div>
            <label>Pickup Address: </label>
            <input
              type="text"
              value={pickupAddress}
              onChange={(e) => setPickupAddress(e.target.value)}
              placeholder="e.g., 123 Main St, City"
            />
          </div>
          <div>
            <label>Drop-off Address: </label>
            <input
              type="text"
              value={dropoffAddress}
              onChange={(e) => setDropoffAddress(e.target.value)}
              placeholder="e.g., 456 Oak St, City"
            />
          </div>
          <div>
            <label>Cab Type: </label>
            <select value={cabType} onChange={(e) => setCabType(e.target.value)}>
              <option value="Economy">Economy</option>
              <option value="Quickest">Quickest (under 2 mins)</option>
              <option value="WaitAndSave">Wait and Save (10-15 mins)</option>
              <option value="Minivan">Minivan (6 seaters)</option>
              <option value="BlackSUV">Black SUV</option>
            </select>
          </div>
          <div className="note">
            Note: For best results, please enter addresses in the format "Street Address, City, State ZIP" (e.g., 123 Main St, Los Angeles, CA 90001).
          </div>
          <button onClick={handleCalculate} className="go-button">Go</button>
        </div>

        {ridesData && calculateTrigger && (
          <div className="animation-container">
            <Lottie
              options={animationOptions[cabType]}
              height={100}
              width={100}
              isStopped={false}
              isPaused={false}
            />
          </div>
        )}

        {ridesData ? (
          <div className="category">
            <h2>Rides</h2>
            <div>
              {ridesData.results
                .filter(r => r.category === 'ride')
                .map(r => (
                  <div key={r.provider} className="provider">
                    <img
                      src={providerLogos[r.provider]}
                      alt={`${r.provider} logo`}
                      className={
                        ridesData.cheapest.ride && ridesData.cheapest.ride.provider === r.provider
                          ? 'cheapest-logo'
                          : ''
                      }
                    />
                    ${r.price.toFixed(2)}
                  </div>
                ))}
            </div>
            <div className="cheapest">
              {ridesData.cheapest.ride ? (
                `Cheapest: ${ridesData.cheapest.ride.provider} at $${ridesData.cheapest.ride.price.toFixed(2)}`
              ) : (
                'No ride prices available'
              )}
            </div>
            <div className="savings">
              {ridesData.savings.ride > 0
                ? `You save $${ridesData.savings.ride.toFixed(2)} by choosing the cheapest!`
                : 'No savings available'}
            </div>
          </div>
        ) : (
          <div>Enter details and press Go to calculate fares...</div>
        )}
      </div>
    </div>
  );
}

export default App;
