import React, { useState } from 'react';
import './App.css';
import UberLogo from './assets/uber-logo.png';
import LyftLogo from './assets/lyft-logo.png';
import EconomyCar from './assets/economy-car.png';
import QuickestCar from './assets/quickest-car.png';
import WaitAndSaveCar from './assets/waitandsave-car.png';
import MinivanCar from './assets/minivan-car.png';
import BlackSUVCar from './assets/blacksuv-car.png';

function App() {
  const [ridesData, setRidesData] = useState(null);
  const [error, setError] = useState(null);
  const [pickupAddress, setPickupAddress] = useState('');
  const [dropoffAddress, setDropoffAddress] = useState('');
  const [cabType, setCabType] = useState('Economy');
  const [loading, setLoading] = useState(false);

  const handleGoClick = async () => {
    setLoading(true);
    setError(null);
    setRidesData(null);

    try {
      const response = await fetch(
        `http://localhost:8002/compare?category=ride&pickup=${encodeURIComponent(pickupAddress)}&dropoff=${encodeURIComponent(dropoffAddress)}&cab_type=${cabType}`
      );
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setRidesData(data);
    } catch (e) {
      console.error('Error fetching prices:', e);
      setError('Failed to fetch prices. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const providerLogos = {
    Uber: UberLogo,
    Lyft: LyftLogo,
  };

  const carImages = {
    Economy: EconomyCar,
    Quickest: QuickestCar,
    WaitAndSave: WaitAndSaveCar,
    Minivan: MinivanCar,
    BlackSUV: BlackSUVCar,
  };

  return (
    <div className="App">
      <h1>Cost Catcher - Ride Price Comparison</h1>

      <div className="tab-content">
        <div className="address-inputs">
          <h3>Enter Ride Details</h3>
          <div>
            <label>Pickup Address: </label>
            <input
              type="text"
              value={pickupAddress}
              onChange={(e) => setPickupAddress(e.target.value)}
              placeholder="e.g., 123 Main St, City"
            />
          </div>
          <div>
            <label>Drop-off Address: </label>
            <input
              type="text"
              value={dropoffAddress}
              onChange={(e) => setDropoffAddress(e.target.value)}
              placeholder="e.g., 456 Oak St, City"
            />
          </div>
          <div>
            <label>Cab Type: </label>
            <select value={cabType} onChange={(e) => setCabType(e.target.value)}>
              <option value="Economy">Economy</option>
              <option value="Quickest">Quickest (under 2 mins)</option>
              <option value="WaitAndSave">Wait and Save (10-15 mins)</option>
              <option value="Minivan">Minivan (6 seaters)</option>
              <option value="BlackSUV">Black SUV</option>
            </select>
          </div>
          <div className="note">
            Note: For best results, please enter addresses in the format "Street Address, City, State ZIP" (e.g., 123 Main St, Los Angeles, CA 90001).
          </div>
          <button className="go-button" onClick={handleGoClick} disabled={loading}>
            {loading ? 'Calculating...' : 'Go'}
          </button>
        </div>

        {ridesData && (
          <div className="car-display">
            <img src={carImages[cabType]} alt={`${cabType} car`} className="car-image" />
          </div>
        )}

        {error && <div className="error">Error: {error}</div>}

        {ridesData && (
          <div className="category">
            <h2>Rides</h2>
            <div>
              {ridesData.results
                .filter(r => r.category === 'ride')
                .map(r => (
                  <div key={r.provider} className="provider">
                    <img
                      src={providerLogos[r.provider]}
                      alt={`${r.provider} logo`}
                      className={
                        ridesData.cheapest.ride && ridesData.cheapest.ride.provider === r.provider
                          ? 'cheapest-logo'
                          : ''
                      }
                    />
                    ${r.price.toFixed(2)}
                  </div>
                ))}
            </div>
            <div className="cheapest">
              {ridesData.cheapest.ride ? (
                `Cheapest: ${ridesData.cheapest.ride.provider} at $${ridesData.cheapest.ride.price.toFixed(2)}`
              ) : (
                'No ride prices available'
              )}
            </div>
            <div className="savings">
              {ridesData.savings.ride > 0
                ? `You save $${ridesData.savings.ride.toFixed(2)} by choosing the cheapest!`
                : 'No savings available'}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
