import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [data, setData] = useState(null);

  useEffect(() => {
    const ws = new WebSocket('ws://localhost:8002/ws/prices');

    ws.onopen = () => {
      console.log('WebSocket connected');
    };

    ws.onmessage = (event) => {
      const result = JSON.parse(event.data);
      setData(result);
    };

    ws.onerror = (error) => {
      console.error('WebSocket error:', error);
    };

    ws.onclose = () => {
      console.log('WebSocket closed');
    };

    return () => {
      ws.close();
    };
  }, []);

  if (!data) return <div>Loading...</div>;

  return (
    <div className="App">
      <h1>Cost Catcher - Price Comparison</h1>
      <div className="category">
        <h2>Rides</h2>
        <div>
          {data.results
            .filter(r => r.category === 'ride')
            .map(r => (
              <div key={r.provider} className="provider">
                {r.provider}: ${r.price.toFixed(2)}
              </div>
            ))}
        </div>
        <div className="cheapest">
          {data.cheapest.ride ? (
            `Cheapest: ${data.cheapest.ride.provider} at $${data.cheapest.ride.price.toFixed(2)}`
          ) : (
            'No ride prices available'
          )}
        </div>
        <div className="savings">
          {data.savings.ride > 0
            ? `You save $${data.savings.ride.toFixed(2)} by choosing the cheapest!`
            : 'No savings available'}
        </div>
      </div>
      <div className="category">
        <h2>Groceries</h2>
        <div>
          {data.results
            .filter(r => r.category === 'grocery')
            .map(r => (
              <div key={r.provider} className="provider">
                {r.provider}: ${r.price.toFixed(2)}
              </div>
            ))}
        </div>
        <div className="cheapest">
          {data.cheapest.grocery ? (
            `Cheapest: ${data.cheapest.grocery.provider} at $${data.cheapest.grocery.price.toFixed(2)}`
          ) : (
            'No grocery prices available'
          )}
        </div>
        <div className="savings">
          {data.savings.grocery > 0
            ? `You save $${data.savings.grocery.toFixed(2)} by choosing the cheapest!`
            : 'No savings available'}
        </div>
      </div>
    </div>
  );
}

export default App;
