import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [activeTab, setActiveTab] = useState('rides'); // Default to Rides tab
  const [ridesData, setRidesData] = useState(null);
  const [groceriesData, setGroceriesData] = useState(null);
  const [error, setError] = useState(null);

  // Rides inputs
  const [pickupAddress, setPickupAddress] = useState('');
  const [dropoffAddress, setDropoffAddress] = useState('');
  const [cabType, setCabType] = useState('Economy'); // Default cab type

  // Groceries inputs
  const [store, setStore] = useState('Walmart'); // Default store
  const [groceryDropoffAddress, setGroceryDropoffAddress] = useState('');

  const [ws, setWs] = useState(null);
  const [isWsReady, setIsWsReady] = useState(false); // Track WebSocket readiness

  useEffect(() => {
    // Create WebSocket connection
    const websocket = new WebSocket('ws://localhost:8002/ws/prices');

    websocket.onopen = () => {
      console.log('WebSocket connected');
      setIsWsReady(true);
      // Send initial message based on active tab
      if (activeTab === 'rides') {
        websocket.send(JSON.stringify({
          category: 'ride',
          pickup: pickupAddress,
          dropoff: dropoffAddress,
          cabType: cabType,
        }));
      } else {
        websocket.send(JSON.stringify({
          category: 'grocery',
          dropoff: groceryDropoffAddress,
          store: store,
        }));
      }
    };

    websocket.onmessage = (event) => {
      console.log('Received WebSocket message:', event.data);
      try {
        const result = JSON.parse(event.data);
        if (result.category === 'ride') {
          setRidesData(result);
        } else if (result.category === 'grocery') {
          setGroceriesData(result);
        }
        setError(null);
      } catch (e) {
        console.error('Error parsing WebSocket message:', e);
        setError('Error parsing price data');
      }
    };

    websocket.onerror = (error) => {
      console.error('WebSocket error:', error);
      setError('WebSocket connection failed');
      setIsWsReady(false);
    };

    websocket.onclose = () => {
      console.log('WebSocket disconnected');
      setError('WebSocket connection closed');
      setIsWsReady(false);
    };

    setWs(websocket);

    // Cleanup on unmount
    return () => {
      websocket.close();
    };
  }, []); // Run once on mount

  // Send updated parameters when inputs or active tab change
  useEffect(() => {
    if (ws && isWsReady) {
      if (activeTab === 'rides') {
        ws.send(JSON.stringify({
          category: 'ride',
          pickup: pickupAddress,
          dropoff: dropoffAddress,
          cabType: cabType,
        }));
      } else {
        ws.send(JSON.stringify({
          category: 'grocery',
          dropoff: groceryDropoffAddress,
          store: store,
        }));
      }
    } else if (ws && !isWsReady) {
      console.log('WebSocket not ready, waiting to send message...');
    }
  }, [pickupAddress, dropoffAddress, cabType, store, groceryDropoffAddress, activeTab, ws, isWsReady]);

  if (error) return <div className="error">Error: {error}</div>;

  return (
    <div className="App">
      <h1>Cost Catcher - Price Comparison</h1>

      {/* Tabs */}
      <div className="tabs">
        <button
          className={`tab-button ${activeTab === 'rides' ? 'active' : ''}`}
          onClick={() => setActiveTab('rides')}
        >
          Rides
        </button>
        <button
          className={`tab-button ${activeTab === 'groceries' ? 'active' : ''}`}
          onClick={() => setActiveTab('groceries')}
        >
          Groceries
        </button>
      </div>

      {/* Rides Tab */}
      {activeTab === 'rides' && (
        <div className="tab-content">
          <div className="address-inputs">
            <h3>Enter Ride Details</h3>
            <div>
              <label>Pickup Address: </label>
              <input
                type="text"
                value={pickupAddress}
                onChange={(e) => setPickupAddress(e.target.value)}
                placeholder="e.g., 123 Main St, City"
              />
            </div>
            <div>
              <label>Drop-off Address: </label>
              <input
                type="text"
                value={dropoffAddress}
                onChange={(e) => setDropoffAddress(e.target.value)}
                placeholder="e.g., 456 Oak St, City"
              />
            </div>
            <div>
              <label>Cab Type: </label>
              <select value={cabType} onChange={(e) => setCabType(e.target.value)}>
                <option value="Economy">Economy</option>
                <option value="Premium">Premium</option>
                <option value="SUV">SUV</option>
              </select>
            </div>
          </div>

          {ridesData ? (
            <div className="category">
              <h2>Rides</h2>
              <div>
                {ridesData.results
                  .filter(r => r.category === 'ride')
                  .map(r => (
                    <div key={r.provider} className="provider">
                      {r.provider}: ${r.price.toFixed(2)}
                    </div>
                  ))}
              </div>
              <div className="cheapest">
                {ridesData.cheapest.ride ? (
                  `Cheapest: ${ridesData.cheapest.ride.provider} at $${ridesData.cheapest.ride.price.toFixed(2)}`
                ) : (
                  'No ride prices available'
                )}
              </div>
              <div className="savings">
                {ridesData.savings.ride > 0
                  ? `You save $${ridesData.savings.ride.toFixed(2)} by choosing the cheapest!`
                  : 'No savings available'}
              </div>
            </div>
          ) : (
            <div>Loading...</div>
          )}
        </div>
      )}

      {/* Groceries Tab */}
      {activeTab === 'groceries' && (
        <div className="tab-content">
          <div className="address-inputs">
            <h3>Enter Grocery Details</h3>
            <div>
              <label>Select Store: </label>
              <select value={store} onChange={(e) => setStore(e.target.value)}>
                <option value="Walmart">Walmart</option>
                <option value="Kroger">Kroger</option>
                <option value="Safeway">Safeway</option>
              </select>
            </div>
            <div>
              <label>Drop-off Address: </label>
              <input
                type="text"
                value={groceryDropoffAddress}
                onChange={(e) => setGroceryDropoffAddress(e.target.value)}
                placeholder="e.g., 456 Oak St, City"
              />
            </div>
          </div>

          {groceriesData ? (
            <div className="category">
              <h2>Groceries</h2>
              <div>
                {groceriesData.results
                  .filter(r => r.category === 'grocery')
                  .map(r => (
                    <div key={r.provider} className="provider">
                      {r.provider}: ${r.price.toFixed(2)}
                    </div>
                  ))}
              </div>
              <div className="cheapest">
                {groceriesData.cheapest.grocery ? (
                  `Cheapest: ${groceriesData.cheapest.grocery.provider} at $${groceriesData.cheapest.grocery.price.toFixed(2)}`
                ) : (
                  'No grocery prices available'
                )}
              </div>
              <div className="savings">
                {groceriesData.savings.grocery > 0
                  ? `You save $${groceriesData.savings.grocery.toFixed(2)} by choosing the cheapest!`
                  : 'No savings available'}
              </div>
            </div>
          ) : (
            <div>Loading...</div>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
